const Sealious = require("sealious");
const path = require("path");
const react = require("react");
var ReactDOMServer = require("react-dom/server");
//const App = require("./views/App");
const config = {
	datastore_mongo: {
		port: 27018,
	},
	core: { environment: "production" },
	smtp: {
		host: "localhost",
		port: 1025,
		user: "any",
		password: "any",
	},

	email: {
		from_name: "Sealious test app",
		from_name: "sealious@example.com",
	},

	upload_path: path.resolve("./uploaded_files"),
};
const manifest = {
	name: "What Seal Do?",
	logo: path.resolve(__dirname, "./img/logo.png"),
	version: "1.0",
	default_language: "pl",
	base_url: "http://localhost:8080",
	admin_email: "mr.seal.api.admin@sealcode.org",
};

const app = new Sealious.App(config, manifest);

const emails = app.createChip(Sealious.Collection, {
	name: "emails",
	fields: [
		{ name: "email", type: "email", required: true },
		{ name: "accepted", type: "boolean", required: true },
		{ name: "removeToken", type: "secret-token", required: true },
	],
	access_strategy: { default: "super", create: "public" },
});

app.addHook(
	new app.Sealious.EventMatchers.Collection({
		when: "before",
		collection_name: "emails",
		action: "create",
	}),
	async (emitted_event, body) => {
		return { ...body, accepted: false, removeToken: " " };
	}
);

app.addHook(
	new app.Sealious.EventMatchers.Collection({
		when: "after",
		collection_name: "emails",
		action: "create",
	}),

	async (emitted_event, resource) => {
		const token = (await app.run_action(
			new app.Sealious.SuperContext(emitted_event.metadata.context),
			["collections", "emails", resource.id],
			"show"
		)).body.removeToken;
		console.log(token);
		const message = await app.EmailTemplates.Simple(app, {
			to: resource.body.email,
			subject: "Potwierdź subskrypcję",
			text: "Dziękujemy, potwierdź subskrypcję klikacjąc w link",
			buttons: [
				{
					text: "Potwierdź",
					href: `http://localhost:8080/confirm-email?token=${token}`,
				},
			],
		});

		message.send(app);
	}
);

function render_form(app, token, email) {
	return `<!DOCTYPE html>
	<html>
	<style>


	html{
		font-size: 1.5em;
		color: #222;
	}

	body{
		padding: 1rem;
		padding-bottom: 2rem;
	}

	* {
		box-sizing: border-box;
		line-height: 1rem;
		font-size: 0.66666rem;
		margin:0;
	}

	p, ul, li{
		max-width: 32rem;
	}

	ul, ol{
		padding-left: 2rem;
	}

	p + p{
		margin-top: 1rem;
	}

	img{
		vertical-align:bottom;
	}

	h1, h2, h3{
		margin-top: 1rem;
		line-height:2rem;
		color: #333;
	}

	h1{
		font-size:1.75rem;
	}

	h2{
		font-size: 1.25rem;
	}

	html {
	    background-color: #edeaea;
	}
	body {
		max-width: 21cm;
		margin: 1cm auto;
		font-family: sans-serif;
		background-color: white;
		padding: 1cm;
		box-sizing: border-box;
	}
	#email {
		font-weight: 700;
	}
	</style>
	<meta charset="utf-8">
	<title>Subskrypcja potwierdzona</title>
	<img src="/api/v1/logo" alt="${app.manifest.name} - logo"/>
	<h1>Twoja subskrypcja została potwierdzona</h1>
	<p>Od tej pory na adres <span id="email">${email}</span> będą wysyłane najnowsze informacje związane z naszą działalnością. </p>



	</html>`;
}

//const markup = ReactDOMServer.renderToString(<App />);
app.WwwServer.custom_route(
	"GET",
	"/confirm-email",
	async (app, context, params) => {
		const result = await app.run_action(
			new app.Sealious.SuperContext(context),
			["collections", "emails"],
			"show",
			{ filter: { removeToken: params.token } }
		);

		await app.run_action(
			new app.Sealious.SuperContext(context),
			["collections", "emails", result.items[0].id],
			"edit",
			{ accepted: true }
		);
		console.log("result", result);
		return new app.Sealious.VirtualFile(
			render_form(app, params.token, result.items[0].body.email),
			"text/html"
		);
	}
);

app.start();
