"use strict";

var React = require("react");

var Loading = function Loading(props) {
	var delay = 65;
	var boxes_amnt = Math.floor(Math.random() * 4) + 4;
	//var boxes_amnt = 6;
	var boxes = [];
	for (var i = 0; i < boxes_amnt; i++) {
		boxes.push(React.createElement("div", {
			className: "loading-box " + props.boxClass,
			style: { animationDelay: delay * i + "ms" }
		}));
		boxes.push(React.createElement("div", { className: "loading-shadow" }));
	}
	var loading = null;
	if (props.text) {
		loading = React.createElement(
			"div",
			{ className: "loading-text" },
			props.text
		);
	}
	return React.createElement(
		"div",
		{ className: "loading-container" },
		React.createElement(
			"div",
			null,
			boxes
		),
		loading
	);
};

/*

*/

module.exports = Loading;